import React, { Component } from 'react';
import { Text, View, StyleSheet, Button, Image, ImageBackground } from 'react-native';
import * as firebase from "firebase";

export default class Profile extends Component {
  state = {
    email: "",
    displayName: ""
  };

  componentDidMount() {
    const { email, displayName } = firebase.auth().currentUser;
    this.setState({ email, displayName });
  }

  signOutUser = () => {
    firebase.auth().signOut();
  };


  render() {
    return (
      <View style={styles.container}>
        <ImageBackground
          style={styles.image}
          blurRadius={4} // must be passed from the parent, the number may vary depending upon your screen size
          source={require('../../assets/simcampus.jpg')}
        >

          <View style={{ marginTop: 25 }}></View>

          <Image
            source={require('../../assets/user.jpg')}
            style={{ width: 150, height: 150, borderRadius: 150 / 2, alignItems: 'center'}}
          />

          <View style={{ marginTop: 10 }}></View>

          <View style={styles.textbox}>
            <Text style={styles.text}>Name: {this.state.displayName} </Text>
            <Text style={styles.text}>Email: {this.state.email} </Text>
            <Text style={styles.text} >UOL SRN: 170280873 </Text>
            <Text style={styles.text}>SIM No.: 10184527 </Text>
          </View>

          <View style={styles.logoutButton}>
            <Button title="Logout" onPress={this.signOutUser}>
            </Button>
          </View>

          <Text style={{ textDecorationStyle: "solid" }}>No outstanding fees for this semester.</Text>

        </ImageBackground>

      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "top",
    // flexDirection: "column",
    alignItems: "center"

  },

  textbox: {
    color: "#000",
    // fontSize: 11,
    fontWeight: '500',
    textAlign: 'center',
    backgroundColor: '#fff'
  },

  image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center",
    width: '100%',
    height: '100%',
  },

  logoutButton: {
    justifyContent: 'flex-end',
    marginTop: 10,
    marginBottom: 50,
    alignSelf: 'center',
  }
});
